mapper.select:
select * from ( SELECT appid code, appid id, APPNAME name, '0' dealType, '' pid, '0' isleaf, 0 levelno, case when APPID='${appId}' then -1 else orderid end orderid, 0 open from dict_t_appregister union all SELECT SUITID , SUITID , SUITNAME , '0' , case when superid='0' then appid else SUPERID end, case when (select count(*) from dict_t_suit where superid=t.SUITID)>0 then '0' else '1' end isleaf, LEVELNO , ORDERID , 0 FROM dict_t_suit t union all select m.TABLEID, m.TABLEID, m.NAME, (select dealname from DICT_T_DEALTYPE where dealid=m.dealtype and (appid = m.appid or appid = '*')), m.SUITID , '1' , (select LEVELNO+1 FROM DICT_T_SUIT WHERE SUITID = M.SUITID) , m.ORDERID, 0 open FROM dict_t_model m
where tabletype = '1' or tabletype = '3'
) CONNECT BY pid = PRIOR id START WITH LEVELNO = 0 order SIBLINGS BY ORDERID ASC

		SELECT APPID, SUITID, SUITNAME, SUITTYPE, SUPERID,ISLEAF, REMARK,LEVELNO,ORDERID FROM dict_t_suit u  

                       1 = 1

