CREATE OR REPLACE FUNCTION F_GET_BGTSUMMARYINFO(docID VARCHAR2, fromDeptID VARCHAR2) RETURN VARCHAR2 IS
 v_return VARCHAR2(4000);
 v_projBugetNum NUMBER(18,4);
 v_basBugetNum NUMBER(18, 4);
 v_firAgencyName VARCHAR2(200);
 v_falg NUMBER(4);
BEGIN
      --判断当前公文是否已经创建任务
      SELECT COUNT(1) INTO v_falg FROM bgt_t_oarelation WHERE docid = docID;
      IF v_falg = 0 THEN
         RETURN v_return;
      END IF;       
      --获取主管部门
      SELECT NAME INTO v_firAgencyName  FROM Code_t_Agency_Bgt WHERE GUID = fromDeptID;
      --单位
      WITH
        AGENCYLIST AS (SELECT *  FROM CODE_T_AGENCY_BGT START WITH GUID = fromDeptID CONNECT BY PRIOR GUID = SUPERGUID),
        --基本支出专项
        BASSPFLIST AS  (SELECT * FROM SPF_T_FBASEINFO F WHERE EXISTS (SELECT 1 FROM CODE_V_EXPPROJECT WHERE ISLEAF = '1'  AND F.SPFID = GUID))
       --基本支出总金额
       SELECT nvl(SUM(budgetnum), 0) INTO v_basBugetNum FROM bgt_t_zcxmmxb WHERE agencyid IN (SELECT GUID FROM AGENCYLIST T1,OA_V_DOCRELATEEXT A1 WHERE A1.DOCID = docID AND A1.FROMDEPTID = T1.GUID) AND spfid IN (SELECT spfid FROM BASSPFLIST) AND PROJECTid <> '*';

      WITH
        AGENCYLIST AS (SELECT *  FROM CODE_T_AGENCY_BGT START WITH GUID = fromDeptID CONNECT BY PRIOR GUID = SUPERGUID),
        --项目支出专项
        PROJSPFLIST AS  (SELECT * FROM SPF_T_FBASEINFO F  WHERE NOT EXISTS (SELECT * FROM CODE_V_EXPPROJECT WHERE ISLEAF = '1' AND F.SPFID = GUID))
       --项目支出总金额
       SELECT nvl(SUM(budgetnum), 0) INTO v_projBugetNum FROM bgt_t_zcxmmxb WHERE agencyid IN (SELECT GUID FROM AGENCYLIST T1,OA_V_DOCRELATEEXT A1 WHERE A1.DOCID = docID AND A1.FROMDEPTID = T1.GUID) AND spfid IN (SELECT spfid FROM PROJSPFLIST) AND projectid <> '*';

      v_return := v_firAgencyName || '安排基本支出'||v_basBugetNum||'万元,安排项目支出'||v_projBugetNum||'万元。其中</br>';


       --基本支出单位数据
       FOR v_data IN (
       WITH
        AGENCYLIST AS (SELECT *  FROM CODE_T_AGENCY_BGT START WITH GUID = fromDeptID CONNECT BY PRIOR GUID = SUPERGUID),
        --基本支出专项
        BASSPFLIST AS  (SELECT * FROM SPF_T_FBASEINFO F WHERE EXISTS (SELECT 1 FROM CODE_V_EXPPROJECT WHERE ISLEAF = '1'  AND F.SPFID = GUID)),
        --项目支出专项
        PROJSPFLIST AS  (SELECT * FROM SPF_T_FBASEINFO F  WHERE NOT EXISTS (SELECT * FROM CODE_V_EXPPROJECT WHERE ISLEAF = '1' AND F.SPFID = GUID))
         SELECT (SELECT NAME FROM AGENCYLIST WHERE GUID = agencyid) AGENCYNAME,
         SUM(CASE WHEN SPFID IN (SELECT SPFID FROM BASSPFLIST) THEN BUDGETNUM ELSE 0 END) BASENUMS,
         SUM(CASE WHEN SPFID IN (SELECT SPFID FROM PROJSPFLIST) THEN budgetnum ELSE 0 END) PROJNUMS FROM BGT_T_ZCXMMXB WHERE AGENCYID IN (SELECT GUID FROM AGENCYLIST T1,OA_V_DOCRELATEEXT A1 WHERE A1.DOCID = docID AND A1.FROMDEPTID = T1.GUID) AND PROJECTid <> '*' GROUP BY AGENCYID)
       LOOP
         v_return := v_return || v_data.AGENCYNAME || '安排基本支出' || v_data.BASENUMS || '万元，安排项目支出' || v_data.PROJNUMS || '万元;</br>';
       END LOOP;

       RETURN v_return;
END F_GET_BGTSUMMARYINFO;

create or replace procedure SPF_GETSUMMARYINFO(p_docId in varchar2, resultData out varchar2) is

v_tmp VARCHAR2(4000);
begin

 for t_tab in (
    select (select name from code_t_firagency where guid = a.FIRAGENCYID) firagencyname,
           a.spfname,
           a.spfid,
           (select count(1) from Spf_t_Pbaseinfo p where p.spfid = a.SPFID and p.ISBGT = '1') inbgtnum,
           (select sum(i.PROVNUM) from Spf_t_Pbaseinfo p,Spf_t_Pinvestplan i where p.spfid = a.SPFID and p.ISBGT = '1' and p.PROJECTID = i.PROJECTID )  auditnum
      from spf_t_fbaseinfo a
     where spfid in (select taskid
                       from spf_t_oarelation
                      where docid = p_docId
                      and tasktype = '0'))
   LOOP
        v_tmp := '';
        for t_det in (
            select dp.projname,
              (select sum(pi.PROVNUM) from spf_t_pinvestplan pi where pi.PROJECTID = dp.PROJECTID) projnum
            from spf_t_pbaseinfo dp
            where dp.spfid =  t_tab.spfid and isbgt = '1'
             and  dp.PROJECTID in (select taskid
                               from spf_t_oarelation
                               where docid = p_docId
                               and tasktype = '1')
          )
          LOOP
             v_tmp := v_tmp||t_det.projname||' 安排预算 '||t_det.projnum || ' 万元 <br>' ;
          end loop;
          IF v_tmp IS NOT NULL THEN
            resultData := resultData||t_tab.firagencyname||'的'||t_tab.spfname||'的项目安排的数量 '||t_tab.inbgtnum|| '个 部门核定金额'||t_tab.auditnum||'其中<br>'||v_tmp ;
          END IF;
   end loop;

end SPF_GETSUMMARYINFO;

BEGIN
    EXECUTE IMMEDIATE Q'{CREATE OR REPLACE FORCE VIEW  BAS_T_CSCJB AS 
  SELECT DATAKEY,ORDERID,NEEDUPDATE,FINYEAR,AGENCYID,ZZRS,RJBZ,ESTID,HJ,ZXID, STATUS, DBVERSION FROM P#BAS_T_CSCJB  WHERE YEAR=global_multyear_cz.Secu_f_GLOBAL_PARM('YEAR')  AND  PROVINCE=global_multyear_cz.Secu_f_GLOBAL_PARM('DIVID')  AND STATUS='1'}';
END;

