  SELECT spf_v_pbaklistmain.FIRAGENCYID AS FIRAGENCYID,spf_v_pbaklistmain.SPFNAME AS SPFNAME,spf_v_pbaklistmain.PROJGROUP_NAME AS PROJGROUP_NAME,spf_v_pbaklistmain.PROJNAME AS PROJNAME,spf_v_pbaklistmain.PROJCODE AS PROJCODE,spf_v_pbaklistmain.DATAKEY AS DATAKEY,spf_v_pbaklistmain.AGENCYID AS AGENCYID,spf_v_pbaklistmain.AGENCYNAME AS AGENCYNAME,spf_v_pbaklistmain.SPFID AS SPFID,spf_v_pbaklistmain.PROJTYPEID AS PROJTYPEID,spf_v_pbaklistmain.PROJECTID AS PROJECTID,spf_v_pbaklistmain.REPORTAGENCY AS REPORTAGENCY,spf_v_pbaklistmain.EXPFUNCCLASS AS EXPFUNCCLASS,spf_v_pbaklistmain.CREATEUSER AS CREATEUSER,spf_v_pbaklistmain.CREATETIME AS CREATETIME,spf_v_pbaklistmain.UPSTATUS AS UPSTATUS,spf_v_pbaklistmain.UPTIME AS UPTIME,spf_v_pbaklistmain.IMPORTANCE AS IMPORTANCE,spf_v_pbaklistmain.DISTRICTID AS DISTRICTID,spf_v_pbaklistmain.ISBGT AS ISBGT,spf_v_pbaklistmain.CHECKSTATUS AS CHECKSTATUS,spf_v_pbaklistmain.PROJAUDITINGSTATUS AS PROJAUDITINGSTATUS,spf_v_pbaklistmain.ISINSTEAD AS ISINSTEAD,spf_v_pbaklistmain.ISPLANDOWN AS ISPLANDOWN,spf_v_pbaklistmain.ISTEMP AS ISTEMP,spf_v_pbaklistmain.TEMPDEPTID AS TEMPDEPTID,spf_v_pbaklistmain.TEMPFIRAGENCYID AS TEMPFIRAGENCYID,spf_v_pbaklistmain.TEMPCHECKSTATUS AS TEMPCHECKSTATUS,spf_v_pbaklistmain.TEMPUPSTATUS AS TEMPUPSTATUS,spf_v_pbaklistmain.TEMPUPTIME AS TEMPUPTIME,spf_v_pbaklistmain.FUNDLEVEL AS FUNDLEVEL,spf_v_pbaklistmain.YX AS YX,spf_v_pbaklistmain.QTZJ AS QTZJ,spf_v_pbaklistmain.APPLYNUM AS APPLYNUM,spf_v_pbaklistmain.COUNTYAGNUM AS COUNTYAGNUM,spf_v_pbaklistmain.COUNTYNUM AS COUNTYNUM,spf_v_pbaklistmain.CITYAGNUM AS CITYAGNUM,spf_v_pbaklistmain.CITYNUM AS CITYNUM,spf_v_pbaklistmain.PROVAGNUM AS PROVAGNUM,spf_v_pbaklistmain.PROVNUM AS PROVNUM,spf_v_pbaklistmain.EXPFUNCID AS EXPFUNCID, STATUS, DBVERSION FROM SPF_V_PBAKLISTMAIN spf_v_pbaklistmain}';
END;

