CREATE OR REPLACE PACKAGE BODY PKG_BGT IS
-- Private type declarations --type <TypeName> is <Datatype>;
-- Private constant declarations --<ConstantName> constant <Datatype> := <Value>;
-- Private variable declarations --<VariableName> <Datatype>;
-- Function and procedure implementations PROCEDURE QUOTASET_P_AGENCY_STND(P_SRCAGENYID VARCHAR2,P_DESTAGENCYID CLOB,P_STNDID VARCHAR2) IS V_AGENCYID VARCHAR2(32); V_STNDID VARCHAR2(32); V_POS NUMBER; V_DELIMITER VARCHAR2(10) DEFAULT ','; V_START NUMBER DEFAULT 1; V_AMOUNT NUMBER; V_POS2 NUMBER; V_START2 NUMBER DEFAULT 1; BEGIN LOOP V_POS := DBMS_LOB.INSTR(P_DESTAGENCYID,V_DELIMITER,V_START); V_POS:= NVL(V_POS,0); EXIT WHEN V_POS < 1; V_AMOUNT := V_POS - V_START; V_AGENCYID := DBMS_LOB.SUBSTR(P_DESTAGENCYID,V_AMOUNT ,V_START ); V_START := V_POS + 1; IF V_AGENCYID IS NULL THEN CONTINUE; END IF; V_START2 := 1; LOOP V_POS2 := INSTR(P_STNDID,V_DELIMITER,V_START2); V_POS2:= NVL(V_POS2,0); EXIT WHEN V_POS2 < 1; V_AMOUNT := V_POS2 - V_START2; V_STNDID := SUBSTR(P_STNDID,V_START2,V_AMOUNT); V_START2 := V_POS2 + 1; IF V_STNDID IS NULL THEN CONTINUE; END IF; EXECUTE IMMEDIATE 'DELETE FROM CAL_T_AGSTANDGRADE WHERE AGENCYID = :V_AGENCYID AND STANDARDID = :V_STNDID' USING V_AGENCYID,V_STNDID; EXECUTE IMMEDIATE 'INSERT INTO CAL_T_AGSTANDGRADE (AGENCYID,STANDARDID,GRADEID) SELECT :V_AGENCYID,STANDARDID,GRADEID FROM CAL_T_AGSTANDGRADE WHERE AGENCYID= :P_SRCAGENYID AND STANDARDID =:V_STNDID' USING V_AGENCYID,P_SRCAGENYID,V_STNDID; END LOOP; END LOOP; END QUOTASET_P_AGENCY_STND;
FUNCTION F_GETTASKID_BY_DOCID(P_DOCIDS CLOB) RETURN VARCHAR2 IS V_ARGS VARCHAR2(32767); V_DOCID VARCHAR2(32); V_TASKID VARCHAR2(32); V_POS NUMBER; BEGIN V_ARGS := DBMS_LOB.SUBSTR(P_DOCIDS,32767); IF V_ARGS IS NULL THEN RETURN ''; END IF; LOOP V_POS := NVL(INSTR(V_ARGS,',',-1,1),0); V_DOCID := SUBSTR(V_ARGS,V_POS + 1); EXIT WHEN V_ARGS IS NULL; IF V_DOCID IS NOT NULL THEN SELECT NVL((SELECT TASKID FROM BGT_T_OARELATION WHERE DOCID = V_DOCID),'') INTO V_TASKID FROM DUAL; IF V_TASKID IS NOT NULL THEN RETURN V_TASKID; END IF; END IF; V_ARGS := SUBSTR(V_ARGS,1,V_POS - 1); END LOOP; RETURN ''; END F_GETTASKID_BY_DOCID;
PROCEDURE P_INIT_ZCXMMXB(P_AGENCYID VARCHAR2) IS V_TABLEID VARCHAR2(32); V_TABLE VARCHAR2(30); V_USERID VARCHAR2(32); V_AGENCYID VARCHAR2(32); V_ISLEAF VARCHAR2(32); V_SQL VARCHAR2(32760); V_NEEDUPDATE VARCHAR2(4000); BEGIN --V_USERID := GLOBAL_MULTYEAR_CZ.SECU_F_GLOBAL_PARM('USER'); --SELECT UPAGENCYID INTO V_AGENCYID FROM SECU_T_USER WHERE USERID = V_USERID; V_AGENCYID := P_AGENCYID; SELECT ISLEAF INTO V_ISLEAF FROM CODE_T_AGENCY_BGT WHERE GUID = V_AGENCYID; SELECT TABLEID,DBTABLENAME INTO V_TABLEID,V_TABLE FROM DICT_T_MODEL WHERE DEALTYPE = '2101'; V_SQL := 'INSERT INTO '||V_TABLE||'(DATAKEY,AGENCYID,FINYEAR,PROJECTID,PROJTYPEID,NEEDUPDATE) SELECT RAWTOHEX(SYS_GUID()) DATAKEY,AGENCYID,FINYEAR,PROJECTID,PROJTYPEID,'''||V_NEEDUPDATE||''' NEEDUPDATE FROM ( SELECT X.AGENCYID,Y.FINYEAR,Z.PROJECTID,Z.PROJTYPEID FROM '; V_NEEDUPDATE := PKG_FORMULA.F_GETNEEDUPDATE_689(V_TABLEID); IF V_ISLEAF = '1' THEN V_SQL := V_SQL||'(SELECT GUID AGENCYID FROM CODE_T_AGENCY_BGT WHERE GUID = '''||V_AGENCYID||''') X, '; ELSE V_SQL := V_SQL||'(SELECT GUID AGENCYID FROM CODE_T_AGENCY_BGT WHERE ISLEAF = ''1'' START WITH SUPERGUID = '''||V_AGENCYID||''' CONNECT BY SUPERGUID = PRIOR GUID) X, '; END IF; V_SQL := V_SQL|| '(SELECT TO_CHAR(EXTRACT(YEAR FROM SYSDATE) + ROWNUM,''FM0000'') FINYEAR FROM USER_OBJECTS WHERE ROWNUM < 4) Y, (SELECT GUID PROJECTID,SUPERGUID PROJTYPEID FROM CODE_V_EXPPROJECT WHERE ISLEAF = ''1'' CONNECT BY SUPERGUID = PRIOR GUID START WITH SUPERGUID = ''###'') Z ) T WHERE NOT EXISTS(SELECT 1 FROM '||V_TABLE||' WHERE AGENCYID = T.AGENCYID AND FINYEAR = T.FINYEAR AND PROJECTID = T.PROJECTID AND PROJTYPEID = T.PROJTYPEID)'; BEGIN EXECUTE IMMEDIATE V_SQL; EXCEPTION WHEN OTHERS THEN DBMS_OUTPUT.PUT_LINE(V_SQL); RAISE; END;
V_SQL := ' BEGIN FOR C_DATA IN ('; IF V_ISLEAF = '1' THEN V_SQL := V_SQL||'(SELECT GUID AGENCYID FROM CODE_T_AGENCY_BGT WHERE GUID = '''||V_AGENCYID||''')'; ELSE V_SQL := V_SQL||'(SELECT GUID AGENCYID FROM CODE_T_AGENCY_BGT WHERE ISLEAF = ''1'' START WITH SUPERGUID = '''||V_AGENCYID||''' CONNECT BY SUPERGUID = PRIOR GUID)'; END IF; V_SQL := V_SQL||') LOOP PKG_FORMULA.P_CALCULATEFORMULA_0('''||V_TABLEID||''',C_DATA.AGENCYID); END LOOP; END;'; BEGIN EXECUTE IMMEDIATE V_SQL; EXCEPTION WHEN OTHERS THEN DBMS_OUTPUT.PUT_LINE(V_SQL); RAISE; END; RETURN; END P_INIT_ZCXMMXB;
PROCEDURE P_BGT_DATA(P_ENTRY CHAR DEFAULT '0', P_FLAG CHAR DEFAULT '0', P_ID CLOB, P_RESULT OUT SYS_REFCURSOR) IS V_SQL VARCHAR2(32000); V_AGENCYID VARCHAR2(32); V_SPFID VARCHAR2(32000); V_DOCID VARCHAR2(32); V_FINYEAR VARCHAR2(32); V_ISQUERYSPF CHAR(1); V_PARM VARCHAR2(32760); V_CUR SYS_REFCURSOR; V_KEY VARCHAR2(100); V_DATA VARCHAR2(500); BEGIN --P_ENTRY = '0' 测算分配 入口,'1' 专项入口,'2' 预算入口 --P_FLAG = '0' 按单位检索，'1' 按专项检索 --P_ID P_FLAG = '0' ，表示单位ID，P_FLAG = '1' 表示专项ID0.
V_PARM := DBMS_LOB.SUBSTR(P_ID, 32000); --测算分配入口只有单位视角 IF P_ENTRY = '0' AND P_FLAG = '0' THEN V_SQL := 'SELECT COLUMNNAME,COLUMNVALUE FROM (' || V_PARM || ')'; OPEN V_CUR FOR V_SQL; LOOP FETCH V_CUR INTO V_KEY, V_DATA; EXIT WHEN V_CUR%NOTFOUND; IF V_KEY = 'SPFID' THEN V_SPFID := V_DATA; END IF; IF V_KEY = 'AGENCYID' THEN V_AGENCYID := V_DATA; END IF; IF V_KEY = 'DOCID' THEN V_DOCID := V_DATA; END IF; IF V_KEY = 'FINYEAR' THEN V_FINYEAR := V_DATA; END IF; IF V_KEY = 'ISQUERYSPF' THEN V_ISQUERYSPF := V_DATA; END IF; END LOOP; CLOSE V_CUR; V_SQL := Q'{--测算分配 --测算分配专项年度汇总 with allo_spf_year as (select agencyid, spfid, finyear, sum(BUDGETNUM) as BUDGETNUM from (select agencyid, spfid, finyear, BUDGETNUM from bgt_t_zcxmmxb where spfid in (SELECT SPFID FROM CDT_T_TASKTYPESPF WHERE TASKTYPEID IN (SELECT TASKTYPEID FROM CDT_T_TASKINFO WHERE TASKID IN (SELECT TASKID FROM BGT_T_OARELATION --公文参数 WHERE DOCID = '}' || V_DOCID || Q'{'))) and projectid <> '*' ) --单位参数 where agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID) group by agencyid, spfid, finyear), --测算分配专项单位汇总 allo_spf_all as (select agencyid, spfid, '1111' as finyear, sum(BUDGETNUM) as BUDGETNUM from allo_spf_year group by agencyid, spfid) --测算分配专项 select basebgt.agencyid, basebgt.AGENCYNAME, PKG_UTILS.SPLIT_STRING2(basebgt.AGENCYNAME,'[',']') AS AGENCYCODE, spf.spfid, spf.spfid as projectid, spf.SPFCODE, spf.SPFCODE as projcode, spf.SPFNAME, spf.PROJTYPEID, finyear, BUDGETNUM, '0' as DATAFLAG, isspf, '2' as SPFTYPE, '2' as ordertype, totalname from ( --测算分配单位汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = allo_spf_all.agencyid) as AGENCYNAME, spfid, finyear, BUDGETNUM, '0' as isspf, '测算分配专项单位汇总' as totalname from allo_spf_all union all --测算分配专项年度汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = allo_spf_year.agencyid) as AGENCYNAME, spfid, finyear, BUDGETNUM, '0' as isspf, '测算分配专项年度汇总' as totalname from allo_spf_year}'; --测算分配专项支出加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ union all --测算分配专项汇总 select FIRAGENCYID, (select name from code_t_firagency where code_t_firagency.guid = FIRAGENCYID) AGENCYNAME, spfid, '0000' as finyear, (select nvl(sum(BUDGETNUM), 0) from allo_spf_year where SPF_T_FBASEINFO.spfid = allo_spf_year.spfid --单位参数 and agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID)), '1' as isspf, '测算分配专项汇总' as totalname from SPF_T_FBASEINFO where spfid in (SELECT SPFID FROM CDT_T_TASKTYPESPF WHERE TASKTYPEID IN (SELECT TASKTYPEID FROM CDT_T_TASKINFO WHERE TASKID IN (SELECT TASKID FROM BGT_T_OARELATION --公文参数 WHERE DOCID = '}' || V_DOCID || Q'{')))) basebgt, SPF_T_FBASEINFO spf where basebgt.spfid = spf.spfid order by PROJTYPEID, SPFCODE, isspf desc, AGENCYCODE, finyear}'; OPEN P_RESULT FOR V_SQL; RETURN; END IF;
--专项入口只有专项视角,需要加入判断，如果是菜单入口，传主管部门/处室的情况 IF P_ENTRY = '1' AND P_FLAG = '1' THEN V_SQL := 'SELECT COLUMNNAME,COLUMNVALUE FROM (' || V_PARM || ')'; OPEN V_CUR FOR V_SQL; LOOP FETCH V_CUR INTO V_KEY, V_DATA; EXIT WHEN V_CUR%NOTFOUND; IF V_KEY = 'SPFID' THEN V_SPFID := V_DATA; END IF; IF V_KEY = 'AGENCYID' THEN V_AGENCYID := V_DATA; END IF; IF V_KEY = 'DOCID' THEN V_DOCID := V_DATA; END IF; IF V_KEY = 'FINYEAR' THEN V_FINYEAR := V_DATA; END IF; IF V_KEY = 'ISQUERYSPF' THEN V_ISQUERYSPF := V_DATA; END IF; END LOOP; CLOSE V_CUR; V_SQL := Q'{--专项视角项目支出 --项目年度汇总 with proj_proj_year as (select agencyid, spfid, projectid, finyear, sum(BUDGETNUM) as BUDGETNUM, '0' ISSPF from (select agencyid, spfid, projectid, finyear, BUDGETNUM from bgt_t_zcxmmxb}'; IF V_AGENCYID IS NOT NULL THEN --主管部门/处室参数 V_SQL := V_SQL || Q'{ where spfid in (select spfid from SPF_T_FBASEINFO where FIRAGENCYID = '}' || V_AGENCYID || Q'{')}'; ELSE --专项参数 V_SQL := V_SQL || Q'{ where spfid in (}' || V_SPFID || ')'; END IF; V_SQL := V_SQL || Q'{ and projectid <> '*') group by spfid, projectid, agencyid, finyear),}'; --项目支出显示明细项目 IF V_ISQUERYSPF = '0' THEN V_SQL := V_SQL || Q'{ --项目汇总 proj_proj_all as (select agencyid, spfid, projectid, '1111' as finyear, sum(BUDGETNUM) as BUDGETNUM, '0' ISSPF from proj_proj_year group by spfid, projectid, agencyid),}'; END IF; V_SQL := V_SQL || Q'{ --专项年度汇总-已分 proj_spf_year as (select (select FIRAGENCYID from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) as agencyid, spfid, finyear, sum(BUDGETNUM) as BUDGETNUM, '1' as ISSPF from proj_proj_year group by spfid, finyear), --专项汇总-已分 proj_spf_all as (select (select FIRAGENCYID from SPF_T_FBASEINFO where spfid = proj_spf_year.spfid) as agencyid, spfid, '1111' as finyear, sum(BUDGETNUM) as BUDGETNUM, '1' as ISSPF from proj_spf_year group by spfid), --专项年度总计 spf_year as (select (select FIRAGENCYID from SPF_T_FBASEINFO where spfid = spf_number.spfid) as agencyid, spfid, '*' as projectid, finyear, sum(BUDGETNUM) as BUDGETNUM, '1' ISSPF from (select spfid, finyear, BUDGETNUM from bgt_t_zcxmmxb}'; IF V_AGENCYID IS NOT NULL THEN --主管部门/处室参数 V_SQL := V_SQL || Q'{ where spfid in (select spfid from SPF_T_FBASEINFO where FIRAGENCYID = '}' || V_AGENCYID || Q'{')}'; ELSE --专项参数 V_SQL := V_SQL || Q'{ where spfid in (}' || V_SPFID || ')'; END IF; V_SQL := V_SQL || Q'{ and projectid = '*') spf_number group by spfid, finyear), --专项总计 spf_all as (select FIRAGENCYID as agencyid, spfid, '*' as projectid, '0000' as finyear, (select nvl(sum(BUDGETNUM), 0) from bgt_t_zcxmmxb where SPF_T_FBASEINFO.spfid = bgt_t_zcxmmxb.spfid and projectid = '*') as BUDGETNUM, '1' ISSPF from SPF_T_FBASEINFO}'; IF V_AGENCYID IS NOT NULL THEN --主管部门/处室参数 V_SQL := V_SQL || Q'{ where spfid in (select spfid from SPF_T_FBASEINFO where FIRAGENCYID = '}' || V_AGENCYID || Q'{')}'; ELSE --专项参数 V_SQL := V_SQL || Q'{ where spfid in (}' || V_SPFID || ')'; END IF; V_SQL := V_SQL || Q'{), --专项待分-年度 spf_year_surplus as (select spf_year.agencyid, spf_year.spfid, '*' as projectid, spf_year.finyear, (spf_year.BUDGETNUM - nvl(proj_spf_year.BUDGETNUM, 0)) as BUDGETNUM, '1' ISSPF from spf_year, proj_spf_year where spf_year.spfid = proj_spf_year.spfid and spf_year.finyear = proj_spf_year.finyear), --专项待分总计 spf_surplus as (select agencyid, spfid, '*' as projectid, '1111' as finyear, sum(BUDGETNUM) as BUDGETNUM, '1' ISSPF from spf_year_surplus group by agencyid, spfid) --ordertype(0-专项总计，1-专项待分，2-专项已分，3-专项项目) select projbgt.agencyid, projbgt.AGENCYNAME, projbgt.spfid, projbgt.projectid, projbgt.spfcode, projbgt.projcode, projbgt.spfname, projbgt.PROJTYPEID, finyear, BUDGETNUM, DATAFLAG, ISSPF, '0' as SPFTYPE, totalname from (}'; --项目支出显示明细项目 IF V_ISQUERYSPF = '0' THEN V_SQL := V_SQL || Q'{ --项目汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = proj_proj_all.agencyid) as AGENCYNAME, spfid, projectid, (case when spfid = projectid then (select spfcode from SPF_T_FBASEINFO where spfid = proj_proj_all.spfid) else (select PROJCODE from SPF_T_PBASEINFO where projectid = proj_proj_all.projectid) end) AS projcode, (select spfcode from SPF_T_FBASEINFO where spfid = proj_proj_all.spfid) AS spfcode, ' ' || (case when spfid = projectid then (select spfname from SPF_T_FBASEINFO where spfid = proj_proj_all.spfid) else (select projname from SPF_T_PBASEINFO where projectid = proj_proj_all.projectid) end) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = proj_proj_all.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '0' as DATAFLAG, '项目汇总' as totalname, '3' as ordertype from proj_proj_all union all --项目年度汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = proj_proj_year.agencyid) as AGENCYNAME, spfid, projectid, (case when spfid = projectid then (select spfcode from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) else (select PROJCODE from SPF_T_PBASEINFO where projectid = proj_proj_year.projectid) end) AS projcode, (select spfcode from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) AS spfcode, ' ' || (case when spfid = projectid then (select spfname from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) else (select projname from SPF_T_PBASEINFO where projectid = proj_proj_year.projectid) end) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '0' as DATAFLAG, '项目年度汇总' as totalname, '3' as ordertype from proj_proj_year}'; --项目加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ union all}'; END IF; V_SQL := V_SQL || Q'{ --专项汇总-已分 select agencyid, (select name from code_t_firagency where code_t_firagency.guid = agencyid) AGENCYNAME, spfid, '*' as projectid, '0' as projcode, (select spfcode from SPF_T_FBASEINFO where spfid = proj_spf_all.spfid) AS spfcode, ' 已分' AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = proj_spf_all.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '1' as DATAFLAG, '专项汇总-已分' as totalname, '2' as ordertype from proj_spf_all union all --专项年度汇总-已分 select agencyid, (select name from code_t_firagency where code_t_firagency.guid = agencyid) AGENCYNAME, spfid, '*' as projectid, '0' as projcode, (select spfcode from SPF_T_FBASEINFO where spfid = proj_spf_year.spfid) AS spfcode, (select spfname from SPF_T_FBASEINFO where spfid = proj_spf_year.spfid) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = proj_spf_year.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '1' as DATAFLAG, '专项年度汇总-已分' as totalname, '2' as ordertype from proj_spf_year}'; --专项年度汇总-已分加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ union all --专项总计 select agencyid, (select name from code_t_firagency where code_t_firagency.guid = agencyid) AGENCYNAME, spfid, '*' as projectid, '0' as projcode, (select spfcode from SPF_T_FBASEINFO where spfid = spf_all.spfid) AS spfcode, (select spfname from SPF_T_FBASEINFO where spfid = spf_all.spfid) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = spf_all.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '0' as DATAFLAG, '专项总计' as totalname, '0' as ordertype from spf_all union all --专项年度总计 select agencyid, (select name from code_t_firagency where code_t_firagency.guid = agencyid) AGENCYNAME, spfid, '*' as projectid, '0' as projcode, (select spfcode from SPF_T_FBASEINFO where spfid = spf_year.spfid) AS spfcode, (select spfname from SPF_T_FBASEINFO where spfid = spf_year.spfid) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = spf_year.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '0' as DATAFLAG, '专项年度总计' as totalname, '0' as ordertype from spf_year}'; --专项年度总计-已分加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ union all --专项待分 select agencyid, (select name from code_t_firagency where code_t_firagency.guid = agencyid) AGENCYNAME, spfid, '*' as projectid, '0' as projcode, (select spfcode from SPF_T_FBASEINFO where spfid = spf_surplus.spfid) AS spfcode, ' 待分' AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = spf_surplus.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '2' as DATAFLAG, '专项待分' as totalname, '1' as ordertype from spf_surplus union all --专项年度待分 select agencyid, (select name from code_t_firagency where code_t_firagency.guid = agencyid) AGENCYNAME, spfid, '*' as projectid, '0' as projcode, (select spfcode from SPF_T_FBASEINFO where spfid = spf_year_surplus.spfid) AS spfcode, (select spfname from SPF_T_FBASEINFO where spfid = spf_year_surplus.spfid) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = spf_year_surplus.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '2' as DATAFLAG, '专项年度待分' as totalname, '1' as ordertype from spf_year_surplus}'; --专项年度待分加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ ) projbgt order by SPFID, ordertype, PROJTYPEID, SPFCODE, projcode, projectid, agencyid, finyear}'; BEGIN OPEN P_RESULT FOR V_SQL; EXCEPTION WHEN OTHERS THEN dbms_output.put_line(v_sql); RAISE; END; RETURN; END IF;
--预算入口只有单位视角 IF P_ENTRY = '2' AND P_FLAG = '0' THEN V_SQL := 'SELECT COLUMNNAME,COLUMNVALUE FROM (' || V_PARM || ')'; OPEN V_CUR FOR V_SQL; LOOP FETCH V_CUR INTO V_KEY, V_DATA; EXIT WHEN V_CUR%NOTFOUND; IF V_KEY = 'SPFID' THEN V_SPFID := V_DATA; END IF; IF V_KEY = 'AGENCYID' THEN V_AGENCYID := V_DATA; END IF; IF V_KEY = 'DOCID' THEN V_DOCID := V_DATA; END IF; IF V_KEY = 'FINYEAR' THEN V_FINYEAR := V_DATA; END IF; IF V_KEY = 'ISQUERYSPF' THEN V_ISQUERYSPF := V_DATA; END IF; END LOOP; CLOSE V_CUR; V_SQL := Q'{----预算单位视角综合 --基本支出专项年度汇总 with base_spf_year as (select agencyid, spfid, finyear, sum(BUDGETNUM) as BUDGETNUM from (select agencyid, spfid, finyear, BUDGETNUM from bgt_t_zcxmmxb where spfid in (SELECT spfid FROM SPF_T_FBASEINFO WHERE PROJTYPEID IN (SELECT PROJTYPEID FROM SPF_T_PROJECTTYPE START WITH PROJTYPEID = '9' CONNECT BY PRIOR PROJTYPEID = SUPERID)) and projectid <> '*') --单位参数 where agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID) group by agencyid, spfid, finyear), --基本支出专项单位汇总 base_spf_all as (select agencyid, spfid, '1111' as finyear, sum(BUDGETNUM) as BUDGETNUM from base_spf_year group by agencyid, spfid), --项目支出项目年度汇总 proj_proj_year as (select agencyid, spfid, projectid, finyear, sum(BUDGETNUM) as BUDGETNUM, '0' ISSPF from (select agencyid, spfid, projectid, finyear, BUDGETNUM from bgt_t_zcxmmxb where spfid in (SELECT spfid FROM SPF_T_FBASEINFO WHERE FUNDMANAGE = '1') and projectid <> '*') --单位参数 where agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID) group by agencyid, spfid, projectid, finyear),}'; --项目支出显示明细项目 IF V_ISQUERYSPF = '0' THEN V_SQL := V_SQL || Q'{ --项目支出项目汇总 proj_proj_all as (select agencyid, spfid, projectid, '1111' as finyear, sum(BUDGETNUM) as BUDGETNUM, '0' ISSPF from proj_proj_year group by agencyid, spfid, projectid),}'; END IF; V_SQL := V_SQL || Q'{ --项目支出专项年度汇总 proj_spf_year as (select (select FIRAGENCYID from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) as agencyid, spfid, finyear, sum(BUDGETNUM) as BUDGETNUM, '1' as ISSPF from proj_proj_year group by agencyid, spfid, finyear), --项目支出专项汇总 proj_spf_all as (select (select FIRAGENCYID from SPF_T_FBASEINFO where spfid = proj_spf_year.spfid) as agencyid, spfid, '0000' as finyear, sum(BUDGETNUM) as BUDGETNUM, '1' as ISSPF from proj_spf_year group by agencyid, spfid), --测算分配专项年度汇总 allo_spf_year as (select agencyid, spfid, finyear, sum(BUDGETNUM) as BUDGETNUM from (select agencyid, spfid, finyear, BUDGETNUM from bgt_t_zcxmmxb where spfid in (SELECT SPFID FROM CDT_T_TASKTYPESPF WHERE TASKTYPEID IN (SELECT TASKTYPEID FROM CDT_T_TASKINFO WHERE TASKID IN (SELECT TASKID FROM BGT_T_OARELATION --公文参数 WHERE DOCID = '}' || V_DOCID || Q'{'))) and projectid <> '*' ) --单位参数 where agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID) group by agencyid, spfid, finyear), --测算分配专项单位汇总 allo_spf_all as (select agencyid, spfid, '1111' as finyear, sum(BUDGETNUM) as BUDGETNUM from allo_spf_year group by agencyid, spfid) --ordertype(0-基本支出，1-项目支出，2-测算分配项目) --基本支出 select basebgt.agencyid, basebgt.AGENCYNAME, PKG_UTILS.SPLIT_STRING2(basebgt.AGENCYNAME,'[',']') AS AGENCYCODE, spf.spfid, spf.spfid as projectid, spf.SPFCODE, spf.SPFCODE as projcode, spf.SPFNAME, spf.PROJTYPEID, finyear, BUDGETNUM, '0' as DATAFLAG, isspf, '1' as SPFTYPE, '0' as ordertype, totalname from ( --基本支出专项单位汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = base_spf_all.agencyid) as AGENCYNAME, spfid, finyear, BUDGETNUM, '0' as isspf, '基本支出专项单位汇总' as totalname from base_spf_all union all --基本支出专项年度汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = base_spf_year.agencyid) as AGENCYNAME, spfid, finyear, BUDGETNUM, '0' as isspf, '基本支出年度汇总' as totalname from base_spf_year}'; --基本支出加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ union all --基本支出专项汇总 select FIRAGENCYID, (select name from code_t_firagency where code_t_firagency.guid = FIRAGENCYID) AGENCYNAME, spfid, '0000' as finyear, (select nvl(sum(BUDGETNUM), 0) from base_spf_year where SPF_T_FBASEINFO.spfid = base_spf_year.spfid --单位参数 and agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID)), '1' as isspf, '基本支出汇总' as totalname from SPF_T_FBASEINFO where spfid in (SELECT spfid FROM SPF_T_FBASEINFO WHERE PROJTYPEID IN (SELECT PROJTYPEID FROM SPF_T_PROJECTTYPE START WITH PROJTYPEID = '9' CONNECT BY PRIOR PROJTYPEID = SUPERID))) basebgt, SPF_T_FBASEINFO spf where basebgt.spfid = spf.spfid union all --项目支出 select projbgt.agencyid, projbgt.AGENCYNAME, PKG_UTILS.SPLIT_STRING2(projbgt.AGENCYNAME,'[',']') AS AGENCYCODE, projbgt.spfid, projbgt.projectid, projbgt.spfcode, projbgt.projcode, projbgt.spfname, projbgt.PROJTYPEID, finyear, BUDGETNUM, '0' as DATAFLAG, ISSPF, '0' as SPFTYPE, '1' as ordertype, totalname from (}'; --项目支出显示明细项目 IF V_ISQUERYSPF = '0' THEN V_SQL := V_SQL || Q'{ --项目支出项目汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = proj_proj_all.agencyid) as AGENCYNAME, spfid, projectid, (case when spfid = projectid then (select spfcode from SPF_T_FBASEINFO where spfid = proj_proj_all.spfid) else (select PROJCODE from SPF_T_PBASEINFO where projectid = proj_proj_all.projectid) end) AS projcode, (select spfcode from SPF_T_FBASEINFO where spfid = proj_proj_all.spfid) AS spfcode, ' ' || (case when spfid = projectid then (select spfname from SPF_T_FBASEINFO where spfid = proj_proj_all.spfid) else (select projname from SPF_T_PBASEINFO where projectid = proj_proj_all.projectid) end) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = proj_proj_all.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '项目支出项目汇总' as totalname from proj_proj_all union all --项目支出项目年度汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = proj_proj_year.agencyid) as AGENCYNAME, spfid, projectid, (case when spfid = projectid then (select spfcode from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) else (select PROJCODE from SPF_T_PBASEINFO where projectid = proj_proj_year.projectid) end) AS projcode, (select spfcode from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) AS spfcode, ' ' || (case when spfid = projectid then (select spfname from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) else (select projname from SPF_T_PBASEINFO where projectid = proj_proj_year.projectid) end) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = proj_proj_year.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '项目支出项目年度汇总' as totalname from proj_proj_year}'; --项目支出项目加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ union all}'; END IF; V_SQL := V_SQL || Q'{ --项目支出专项汇总 select agencyid, (select name from code_t_firagency where code_t_firagency.guid = agencyid) AGENCYNAME, spfid, '*' as projectid, '0' as projcode, (select spfcode from SPF_T_FBASEINFO where spfid = proj_spf_all.spfid) AS spfcode, (select spfname from SPF_T_FBASEINFO where spfid = proj_spf_all.spfid) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = proj_spf_all.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '项目支出专项汇总' as totalname from proj_spf_all union all --项目支出专项年度汇总 select agencyid, (select name from code_t_firagency where code_t_firagency.guid = agencyid) AGENCYNAME, spfid, '*' as projectid, '0' as projcode, (select spfcode from SPF_T_FBASEINFO where spfid = proj_spf_year.spfid) AS spfcode, (select spfname from SPF_T_FBASEINFO where spfid = proj_spf_year.spfid) AS spfname, (select PROJTYPEID from SPF_T_FBASEINFO where spfid = proj_spf_year.spfid) AS PROJTYPEID, finyear, BUDGETNUM, ISSPF, '项目支出专项年度汇总' as totalname from proj_spf_year}'; --项目支出专项加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ ) projbgt union all --测算分配专项 select basebgt.agencyid, basebgt.AGENCYNAME, PKG_UTILS.SPLIT_STRING2(basebgt.AGENCYNAME,'[',']') AS AGENCYCODE, spf.spfid, spf.spfid as projectid, spf.SPFCODE, spf.SPFCODE as projcode, spf.SPFNAME, spf.PROJTYPEID, finyear, BUDGETNUM, '0' as DATAFLAG, isspf, '2' as SPFTYPE, '2' as ordertype, totalname from ( --测算分配单位汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = allo_spf_all.agencyid) as AGENCYNAME, spfid, finyear, BUDGETNUM, '0' as isspf, '测算分配专项单位汇总' as totalname from allo_spf_all union all --测算分配专项年度汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = allo_spf_year.agencyid) as AGENCYNAME, spfid, finyear, BUDGETNUM, '0' as isspf, '测算分配专项年度汇总' as totalname from allo_spf_year}'; --测算分配专项支出加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ union all --测算分配专项汇总 select FIRAGENCYID, (select name from code_t_firagency where code_t_firagency.guid = FIRAGENCYID) AGENCYNAME, spfid, '0000' as finyear, (select nvl(sum(BUDGETNUM), 0) from allo_spf_year where SPF_T_FBASEINFO.spfid = allo_spf_year.spfid --单位参数 and agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID)), '1' as isspf, '测算分配专项汇总' as totalname from SPF_T_FBASEINFO where spfid in (SELECT SPFID FROM CDT_T_TASKTYPESPF WHERE TASKTYPEID IN (SELECT TASKTYPEID FROM CDT_T_TASKINFO WHERE TASKID IN (SELECT TASKID FROM BGT_T_OARELATION --公文参数 WHERE DOCID = '}' || V_DOCID || Q'{')))) basebgt, SPF_T_FBASEINFO spf where basebgt.spfid = spf.spfid order by ordertype, spftype, PROJTYPEID, SPFCODE, projcode, projectid, isspf desc, AGENCYCODE, finyear}'; BEGIN --dbms_output.put_line(v_sql); OPEN P_RESULT FOR V_SQL; EXCEPTION WHEN OTHERS THEN dbms_output.put_line(v_sql); RAISE; END; RETURN; END IF;
--预算入口只有单位视角,只编制基本支出 IF P_ENTRY = '3' AND P_FLAG = '0' THEN V_SQL := 'SELECT COLUMNNAME,COLUMNVALUE FROM (' || V_PARM || ')'; OPEN V_CUR FOR V_SQL; LOOP FETCH V_CUR INTO V_KEY, V_DATA; EXIT WHEN V_CUR%NOTFOUND; IF V_KEY = 'SPFID' THEN V_SPFID := V_DATA; END IF; IF V_KEY = 'AGENCYID' THEN V_AGENCYID := V_DATA; END IF; IF V_KEY = 'DOCID' THEN V_DOCID := V_DATA; END IF; IF V_KEY = 'FINYEAR' THEN V_FINYEAR := V_DATA; END IF; IF V_KEY = 'ISQUERYSPF' THEN V_ISQUERYSPF := V_DATA; END IF; END LOOP; CLOSE V_CUR; V_SQL := Q'{----预算单位视角基本支出 --基本支出专项年度汇总 with base_spf_year as (select agencyid, spfid, finyear, sum(BUDGETNUM) as BUDGETNUM from (select agencyid, spfid, finyear, BUDGETNUM from bgt_t_zcxmmxb where spfid in (SELECT spfid FROM SPF_T_FBASEINFO WHERE PROJTYPEID IN (SELECT PROJTYPEID FROM SPF_T_PROJECTTYPE START WITH PROJTYPEID = '9' CONNECT BY PRIOR PROJTYPEID = SUPERID)) and projectid <> '*') --单位参数 where agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID) group by agencyid, spfid, finyear), --基本支出专项单位汇总 base_spf_all as (select agencyid, spfid, '1111' as finyear, sum(BUDGETNUM) as BUDGETNUM from base_spf_year group by agencyid, spfid) --ordertype(0-基本支出，1-项目支出，2-测算分配项目) --基本支出 select basebgt.agencyid, basebgt.AGENCYNAME, PKG_UTILS.SPLIT_STRING2(basebgt.AGENCYNAME,'[',']') AS AGENCYCODE, spf.spfid, spf.spfid as projectid, spf.SPFCODE, spf.SPFCODE as projcode, spf.SPFNAME, spf.PROJTYPEID, finyear, BUDGETNUM, '0' as DATAFLAG, isspf, '1' as SPFTYPE, '0' as ordertype, totalname from ( --基本支出专项单位汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = base_spf_all.agencyid) as AGENCYNAME, spfid, finyear, BUDGETNUM, '0' as isspf, '基本支出专项单位汇总' as totalname from base_spf_all union all --基本支出专项年度汇总 select agencyid, (select name from code_t_agency_spf where code_t_agency_spf.guid = base_spf_year.agencyid) as AGENCYNAME, spfid, finyear, BUDGETNUM, '0' as isspf, '基本支出年度汇总' as totalname from base_spf_year}'; --基本支出加入年度过滤 IF V_FINYEAR IS NOT NULL THEN V_SQL := V_SQL || Q'{ where finyear = '}' || V_FINYEAR || ''''; END IF; V_SQL := V_SQL || Q'{ union all --基本支出专项汇总 select FIRAGENCYID, (select name from code_t_firagency where code_t_firagency.guid = FIRAGENCYID) AGENCYNAME, spfid, '0000' as finyear, (select nvl(sum(BUDGETNUM), 0) from base_spf_year where SPF_T_FBASEINFO.spfid = base_spf_year.spfid --单位参数 and agencyid in (select guid from code_t_agency_spf start with GUID = '}' || V_AGENCYID || Q'{' connect by prior GUID = SUPERGUID)), '1' as isspf, '基本支出汇总' as totalname from SPF_T_FBASEINFO where spfid in (SELECT spfid FROM SPF_T_FBASEINFO WHERE PROJTYPEID IN (SELECT PROJTYPEID FROM SPF_T_PROJECTTYPE START WITH PROJTYPEID = '9' CONNECT BY PRIOR PROJTYPEID = SUPERID))) basebgt, SPF_T_FBASEINFO spf where basebgt.spfid = spf.spfid order by ordertype, spftype, PROJTYPEID, SPFCODE, projcode, projectid, isspf desc, AGENCYCODE, finyear}'; --dbms_output.put_line(v_SQL); OPEN P_RESULT FOR V_SQL; RETURN; END IF; END P_BGT_DATA;
BEGIN RETURN; END PKG_BGT;

CREATE OR REPLACE PACKAGE BODY PKG_SPF IS

  -- PRIVATE TYPE DECLARATIONS
  --TYPE <TYPENAME> IS <DATATYPE>;

  -- PRIVATE CONSTANT DECLARATIONS
  --<CONSTANTNAME> CONSTANT <DATATYPE> := <VALUE>;

  -- PRIVATE VARIABLE DECLARATIONS
  --<VARIABLENAME> <DATATYPE>;

  -- FUNCTION AND PROCEDURE IMPLEMENTATIONS
  FUNCTION F_GETTASKID_BY_DOCID(P_DOCIDS CLOB,P_TASKTYPE VARCHAR2) RETURN DATALIST PIPELINED IS
    T_DATA T_RECORD;
    V_TASKID VARCHAR2(32);
    V_DOCID VARCHAR2(32);
    V_POS NUMBER;
    V_ARGS VARCHAR2(32767);
    V_TMP VARCHAR2(4000);
  BEGIN
    V_ARGS := DBMS_LOB.SUBSTR(P_DOCIDS,32767);
    IF V_ARGS IS NULL THEN
      RETURN;
    END IF;
    LOOP
      EXIT WHEN V_ARGS IS NULL;
      V_POS := INSTR(V_ARGS,'^');
      IF V_POS > 0 THEN
        V_TMP := SUBSTR(V_ARGS,1,V_POS - 1);
        V_ARGS := SUBSTR(V_ARGS,V_POS + 1);
      ELSE
        V_TMP := V_ARGS;
        V_ARGS := '';
      END IF;
      LOOP
        EXIT WHEN V_TMP IS NULL;
        V_POS := INSTR(V_TMP,',');
        IF V_POS > 0 THEN
          v_DOCID := SUBSTR(V_TMP,1,V_POS - 1);
          V_TMP := SUBSTR(V_TMP,V_POS + 1);
        ELSE
          V_DOCID := V_TMP;
          V_TMP := '';
        END IF;
        V_TASKID := '';
        FOR C_TSK IN (SELECT TASKID FROM SPF_T_OARELATION WHERE DOCID = V_DOCID AND TASKTYPE = P_TASKTYPE) LOOP
          V_TASKID := C_TSK.TASKID;
          T_DATA.CID := V_TASKID;
          PIPE ROW(T_DATA);
        END LOOP;
        IF V_TASKID IS NOT NULL THEN
          EXIT;
        END IF;
      END LOOP;
    END LOOP;
    RETURN;
  END F_GETTASKID_BY_DOCID;

  FUNCTION F_GETSQLFORZCXMMXB RETURN CLOB IS
    V_TABLEID VARCHAR2(32);
    V_TABLE VARCHAR2(30);
    V_SQL VARCHAR2(32760);
    V_TMP VARCHAR2(4000);
    V_COLLIST VARCHAR2(4000);
  BEGIN
    SELECT TABLEID,DBTABLENAME INTO V_TABLEID,V_TABLE FROM DICT_T_MODEL WHERE DEALTYPE = '2101';
    V_TMP := ' ';
    FOR C_DATA IN (SELECT X.FUNDSOURCENAME, Y.TOBASECOL, X.CODE, X.FUNDSOURCEID
          FROM BGT_V_PUBFUNDSOURCE X, EXP_S_PROJINVEST Y, DICT_T_FACTOR Z
         WHERE X.FUNDSOURCEID = Y.FUNDSOURCEID
           AND Y.TOBASECOL = Z.DBCOLUMNNAME
           AND Z.TABLEID = V_TABLEID
           AND Z.ISLEAF = '1') LOOP
       V_TMP :=V_TMP||'
       '||C_DATA.TOBASECOL||' AS '''||C_DATA.FUNDSOURCEID||''',';
    END LOOP;
    V_TMP := SUBSTR(V_TMP,1,LENGTH(V_TMP) - 1);
    SELECT CONNSTRA(COLUMN_NAME||',') INTO V_COLLIST FROM USER_TAB_COLUMNS WHERE TABLE_NAME = V_TABLE AND COLUMN_NAME NOT IN (
    SELECT Y.TOBASECOL
          FROM BGT_V_PUBFUNDSOURCE X, EXP_S_PROJINVEST Y, DICT_T_FACTOR Z
         WHERE X.FUNDSOURCEID = Y.FUNDSOURCEID
           AND Y.TOBASECOL = Z.DBCOLUMNNAME
           AND Z.TABLEID = V_TABLEID
           AND Z.ISLEAF = '1') AND COLUMN_NAME <> 'PROJECTNAME';
    --V_COLLIST := SUBSTR(V_COLLIST,1,LENGTH(V_COLLIST) - 1);

    V_SQL := 'SELECT '||V_COLLIST||'FUNDSOURCEID,AMT 
    FROM '||V_TABLE||' 
    UNPIVOT(AMT FOR FUNDSOURCEID IN('||V_TMP||'
    ))';
    V_SQL := 'SELECT '||V_COLLIST||'FUNDSOURCEID,AMT,
    (CASE  WHEN SPFID = PROJECTID THEN(SELECT SPFNAME FROM SPF_T_FBASEINFO WHERE SPFID = TTT.SPFID)
    ELSE (SELECT PROJNAME FROM SPF_T_PBASEINFO WHERE PROJECTID = TTT.PROJECTID) END) AS PROJECTNAME,
    (SELECT SPFNAME FROM SPF_T_FBASEINFO WHERE SPFID = TTT.SPFID) AS SPFNAME 
    FROM (
    '||V_SQL||') TTT';
    RETURN V_SQL;
  END F_GETSQLFORZCXMMXB;

BEGIN
  -- INITIALIZATION
  RETURN;
END PKG_SPF;
