mapper.update:
BEGIN
merge into PUB_T_PARTITION_DIVID a using dual b on (a.districtid = #{partition} and a.year = #{year}) when MATCHED then update set a.id_tabspace = (SELECT TABLESPACE_NAME FROM USER_TAB_PARTITIONS WHERE TABLE_NAME = 'P#DICT_T_MODEL' AND PARTITION_NAME = 'P87') when NOT MATCHED then insert (a.districtid, a.id_tabspace, a.year) values (#{partition}, (SELECT TABLESPACE_NAME FROM USER_TAB_PARTITIONS WHERE TABLE_NAME = 'P#DICT_T_MODEL' AND PARTITION_NAME = 'P87'), #{year})
;END;


BEGIN
	SYS_P_COPY_DATA(
		#{sourceDistrictid, mode=IN, jdbcType=VARCHAR},
		#{sourceYear, mode=IN, jdbcType=VARCHAR},
		#{targetDistrictid, mode=IN, jdbcType=VARCHAR},
		#{targetYear, mode=IN, jdbcType=VARCHAR}
	);
END;
